mapper.update:
update SYS_USER set
	SKIN = #{SKIN}
where USERNAME = #{USERNAME}

update SYS_USER set
	LAST_LOGIN = #{LAST_LOGIN}
where 
	USER_ID = #{USER_ID}

mapper.select:
select * from SYS_USER where 1=1
and ( (USERNAME = #{USERNAME} and PASSWORD = #{PASSWORD}) or (EMAIL = #{USERNAME} and PASSWORD = #{PASSWORD}) or (NUMBER = #{USERNAME} and PASSWORD = #{PASSWORD}) )
and USER_ID = #{USER_ID}

mapper.update:
update
	SYS_USER
set
	IP = #{IP}
where
	USERNAME = #{USERNAME}

update SYS_USER set USERNAME = #{USERNAME}, NAME = #{NAME}, ROLE_ID = #{ROLE_ID}, BZ = #{BZ}, EMAIL = #{EMAIL}, NUMBER = #{NUMBER}, PHONE = #{PHONE}, UNIT_ID = #{unit}, IS_SUPER_ADMIN =#{is_super_admin}, association_id = #{association_id}, organization_id = #{organization_id}, volunteer_unit_id = #{volunteer_unit_id}
,PASSWORD = #{PASSWORD}
where USER_ID = #{USER_ID}

mapper.select:
select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.NUMBER, u.PHONE, u.IS_SUPER_ADMIN, r.ROLE_ID, r.ROLE_NAME from SYS_USER u, SYS_ROLE r where u.ROLE_ID = r.ROLE_ID and u.USERNAME != 'admin'
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') )
and u.ROLE_ID=#{pd.ROLE_ID}
and u.LAST_LOGIN >= #{pd.lastLoginStart}
and u.LAST_LOGIN <= #{pd.lastLoginEnd}
order by u.LAST_LOGIN desc

select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.NUMBER, u.PHONE, u.IS_SUPER_ADMIN, r.ROLE_ID, r.ROLE_NAME from SYS_USER u, SYS_ROLE r where u.ROLE_ID = r.ROLE_ID and u.USERNAME != 'admin'
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') )
and u.ROLE_ID=#{ROLE_ID}
and u.LAST_LOGIN >= #{lastLoginStart}
and u.LAST_LOGIN <= #{lastLoginEnd}
order by u.LAST_LOGIN desc

select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, m.unit_id, m.UNIT_NAME_FULL, u.IP, u.EMAIL, u.NUMBER, u.PHONE, r.ROLE_ID, r.ROLE_NAME, m.DEL_FLG from SYS_USER u left join m_unit m on u.unit_id = m.unit_id, SYS_ROLE r where u.ROLE_ID = r.ROLE_ID and u.USERNAME != 'admin'
and (m.UNIT_ID = #{pd.UNIT_ID} or m.UNIT_PATH like '${pd.UNIT_ID}|%' or m.UNIT_PATH like '%|${pd.UNIT_ID}|%')
and u.unit_id = #{pd.unit_id}
and r.ROLE_ID = #{pd.ROLE_ID}
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') )
order by u.LAST_LOGIN desc

mapper.update:
update  SYS_USER
	set 
		PASSWORD = #{PASSWORD}
	where 
		USER_ID = #{USER_ID}
